void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0);
    vec2 M = vec2(cos(iTime * 0.5), sin(iTime)) * 0.4;

    float c = length((uv - M) * 2.0);

    float n = 6.0;
    uv *= n;
    vec2 pid = floor(uv);
    uv = fract(uv);

    col += vec3(0.8, 0.3, 0.1) - c * pow(uv.x, uv.y);

    // Output to screen
    fragColor = vec4(col,1.0);
}
