// Public Domain under http://unlicense.org, see link for details.

// See https://www.redblobgames.com/grids/hexagons/ for context.
// The algorithm is nearly equivalent to
// https://www.redblobgames.com/grids/hexagons/more-pixel-to-hex.html#justin-pombrio
// but derived independently.

// Returns point on the plane u+v+w=0 from
// its x,y coordinates in specific
// Cartesian 2D coordinate system on that plane.
vec3 pixel2cube(vec2 xy)
{
    const float A=1.0/sqrt(3.0),B=1.0/3.0;
    return mat2x3( // WARNING: column-major!
        +A,0.0,-A,
        -B,B+B,-B)*xy;
}

// Returns containing hexagon location in cube coordinates.
// May easily return ivec3 instead.
vec3 cube_round(vec3 uvw)
{
#if 0
    // Concise but may violate constraint q+r+s=0 due to
    // floating point accuracy, and also inefficient.
    return round((floor(uvw.zxy-uvw)-floor(uvw-uvw.yzx))/3.0);
#else
    float xy=floor(uvw.x-uvw.y);
    float yz=floor(uvw.y-uvw.z);
    float zx=floor(uvw.z-uvw.x);
    float q=round((zx-xy)/3.0);
    float r=round((xy-yz)/3.0);
    float s=-q-r;
    return vec3(q,r,s);
#endif
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    // Pixel coordinates.
    vec2 xy=(fragCoord-iResolution.xy/2.0)/iResolution.x;

    // Scale.
    xy*=15.0+10.0*sin(0.37*iTime);
    xy+=10.0*vec2(sin(0.17*iTime),cos(0.17*iTime));

    // Float-point cube coordinates.
    vec3 uvw=pixel2cube(xy);

    // Integer cube coordinates.
    vec3 qrs=cube_round(uvw);

    // Output to screen.
    fragColor=vec4(0.5*sin(28.0*qrs)+0.5,1.0);
}
