vec3 palette( float t ) {
    vec3 a = vec3(0.470, 0.780, 0.700);
    vec3 b = vec3(0.320, 0.160, 0.000);
    vec3 c = vec3(0.440, 3.138, 1.320);
    vec3 d = vec3(0.000, 0.943, 0.747);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0 - iResolution.xy)/iResolution.y;
    vec2 mouse = (iMouse.xy - .5 * iResolution.xy) / iResolution.y;
    vec2 uv0=uv;
    vec2 uv1=uv;
    vec2 uv2=uv;
    float r;
    float g;
    float b;
    float iris;
    float retina;
    float fluct;
    float fluct2;
    float fluct3;
    vec3 color;
    float sclera=length(uv)+.09;
    float topeyelids;
    float boteyelids;


    float movex=sin(iTime*1.5);
    float movey=-cos(iTime);

    /*movex=-mouse.x*1.3;
    movey=-mouse.y*2.7;*/

    float dilatation=smoothstep(.0,1.0,iTime);
    vec2 nuv1=vec2(0.0)- uv;
    float nuv2=uv1.x*sin(iTime*1.1)+uv1.y*cos(iTime);

    uv0.x+=.48*movex;
    uv0.y+=.40*movey;

    float ang = atan(uv0.x,uv0.y);

    fluct= abs(cos(ang*5.-iTime*2.2))*(sin(iTime*0.9));
    fluct2= abs(sin(ang*5.-iTime*2.0))*(sin(uv0.y));
    fluct3= abs(cos(ang*5.-iTime*1.8))*(sin(uv0.x));

    sclera=smoothstep(.9,1.0,sclera);


    uv2.x+=.48*movex-0.09;
    uv2.y+=.40*movey-0.17;
    retina=length(uv2)*dilatation;
    retina=smoothstep(.11,.05,retina);


    iris=length(uv0);
    iris=smoothstep(.29,.3,iris);    

    b=fluct;
    r=fluct2;
    g=fluct3;

    color=palette(r+g+b);

    float bot = 1.95;
    float top = 1.3*sin(iTime*0.5);


    float oval = (uv.x * uv.x) / (bot * bot) + (uv.y * uv.y) / (top * top);

    float eyelidsMask = smoothstep(0.6,1.0, oval);

    vec3 eyelidsColor = vec3(0.000,0.000,0.000); // eyelids color
    vec3 backgroundColor = vec3(1);

    vec3 eyelids = mix(backgroundColor, eyelidsColor, eyelidsMask);

    color=(color*iris+retina+(sclera*2.))*eyelids;

    fragColor=vec4(color,1.00);
}
