vec3 palette(float t)
{

//[[0.758 0.318 0.808] [-0.342 0.558 0.828] [1.090 2.004 1.090] [-0.605 -0.205 1.325]]

    vec3 a = vec3(0.758, 1.318, 1.808);
    vec3 b = vec3(-0.342, 1.558, 0.828);
    vec3 c = vec3(1.000, 2.004, 1.090);
    vec3 d = vec3(-0.605, -0.205, 6.325);

    return a + b * cos(6.28318*(c*t+d) );

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for(float i = 0.0; i < 4.5; i++){

        uv = fract(uv * 1.3) - 0.5;


        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i *.8 + iTime*.0001);

        d = sin(d*8. + iTime)/8.;
        d = abs(d);

        d = pow(0.02 / d, 0.6);

        finalColor += col *= d;

    }

    // Output to screen
    fragColor = vec4((finalColor) - 6.5, 1.0);


}
