vec3 colorize(float its) {
    float x = mod(its*6.0, 6.0);
    return vec3((x<4.50)?smoothstep(4.0, 0.4, x):smoothstep(0.0, 1.4, x-4.6),
                (x<4.25)?smoothstep(2.2, 0.1, x):smoothstep(0.0, 2.1, x-3.9),
                (x<2.00)?smoothstep(1.1, 0.0, x):smoothstep(0.0, 4.0, x-2.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = fragCoord.x/iResolution.x;
    vec3 col = colorize(x);
    fragColor = vec4(col,1.0);
}
