void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    float a = 1.;
    vec2 m = iMouse.y <1. ? vec2(.44) : iMouse.xy/iResolution.xy;
    float s = abs(fract(iTime*.1)-.5)*16.;
    for(float i; i < 8.; i++){
        uv -= m*.2*i;
        uv = abs(uv/a);
        a *= dot(uv-m*s,uv);
    }

    fragColor = vec4(cos(.25*(3.+iTime+vec3(1./a)+vec3(1.,4.,7.)*1.5))*.5+.5,1.0);
}
