#define BALLS 20.
#define CONTRAST 1
#define GLOW .1
#define ORB_SIZE 0.492519
#define PI 3.14159265359


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 xy = 2. * fragCoord / iResolution.xy - 1.0; 
    xy.x *= iResolution.x / iResolution.y;
    vec3 color = vec3(0.0);
    float d = 0.0;

    xy *= 0.9; 
    d = length(abs(xy)-.3 ); //Distance field that creates the four circles


//Modified Color Warp Section by yozic: https://www.shadertoy.com/view/wl2yRD
  for (float i = 0.; i < BALLS; i++) {
    float t = iTime/20.5- i * PI / BALLS * cos(iTime / i);
    vec2 p = vec2(cos(t), sin(t)) / sin(i / BALLS + PI / d * iTime);
    vec3 c = cos(vec3(9, -3, 20) * PI * 2. / PI + PI * (iTime / (i+1.) / 5.)) * (GLOW) + (GLOW);
    fragColor += (vec4(vec3(fract(d * .3 / length(xy - p * ORB_SIZE)) * c), 1.0) ); 

  }
   fragColor.xyz = pow(fragColor.xyz, vec3(CONTRAST));


}
