void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - iResolution.xy*.5)/iResolution.y;

    float u = smoothstep(.2-cos(iTime)/10., .21-cos(iTime)/10., length(vec2(uv.x + sin(iTime)/1.5, uv.y)));
    float u2 = smoothstep(.15-cos(iTime)/10., .16-cos(iTime)/10., length(vec2(uv.x + sin(iTime)/3.5, uv.y + sin(iTime)/ 5.)));

    vec3 order_flip = vec3(0);

    if (cos(iTime)/10. >= .0) {
        u2 *= u += .5;
    }
    else {
        u *= u2 += .5;
    }
    order_flip += u * u2;
    fragColor = vec4(order_flip, 1.0);
}
