// License: CC0

float estimate_alpha(vec3 I){
    return clamp(0.8 + dot(vec3(1.5, -2.1, 1.2), I), 0.0, 1.0);
}

vec3 estimate_foreground(vec3 I, float alpha){
    vec3 B = vec3(0.15, 0.5, 0.1);
    return (I - (1.0 - alpha) * B) / (alpha + 0.1);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord/iResolution.xy;
    vec3 I = texture(iChannel0, uv).rgb;
    float alpha = estimate_alpha(I);
    vec3 F = estimate_foreground(I, alpha);
    vec3 B_new = texture(iChannel1, uv + vec2(fract(-0.2 * iTime), 0)).rgb;
    fragColor = vec4(mix(B_new, F, alpha), 1.0);
}
