void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0, 0, 0);
    //col.x = 0.5 + 0.5*sin(iTime+uv.xyx+vec3(0,2,4));
    col.x = mix(0.0f, cos(iTime * 1.0f), 1.0f);
    col.y = mix(0.0f, sin(iTime * 1.0f), 0.5f);
    col.y = mix(0.0f, sin(iTime * 1.0f), 0.7f);

    // Output to screen
    fragColor = vec4(col, 1.0f);
}
