void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouseUV = iMouse.xy / iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 webcam = texture(iChannel0, uv).rgb;


    float angle = 1.0*iTime * 3.14159/180.0;
    mat3 texcoordTransform = mat3(cos(angle), -sin(angle), 0,
                                  sin(angle),  cos(angle), 0,
                                  0, 0, 1);
    uv = (texcoordTransform * vec3(uv, 0.0)).xy;

    if (length(webcam) < 0.9)
        col *= webcam;
    else
        col += texture(iChannel1, uv * 2.0).rgb;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
