precision highp float;

const float PI = 3.14;
const float e = 2.71;

const float R = 0.8 * e/PI;

const vec4 color1 = vec4(0., 0., 1., 1.);
const vec4 color2 = vec4(1., 0., 0., 1.);

const vec2 pointA = vec2(-R, 0.);
const vec2 pointB = vec2(R, 0.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;

    float r = length(uv);

    float angleC = iTime * R;
    vec2 pointC = vec2(R * cos(angleC), R * sin(angleC));
    vec4 color = (color1 - color2) * sin(atan(uv.y, uv.x) + PI/2. + iTime/2.) * 0.4 + (color1 + color2) * 0.6;
    float s = pow(e, -abs(distance(uv, pointC)) * 10.);

    vec3 circleColor = vec3(pow(e, -abs(R - r) * 10.));
    vec3 snakeColor = vec3(s);

    vec3 snakeHeadColor = vec3(pow(e, -abs(distance(uv, pointC)) * 20.));

    fragColor = color * (vec4(circleColor, 1.) + vec4(snakeColor, 1.));
}
