#define PI 3.14159265
#define TWO_PI 6.2831853

// https://www.shadertoy.com/view/fd3SRf
float sdHexagon( vec2 p, float s, float r ) {
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*s, k.z*s), s);
    return length(p)*sign(p.y) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy*0.5)/iResolution.y;

    float h = smoothstep(0.1, 0.105, sdHexagon(uv,0.3,-0.1));

    vec3 col = vec3(h);

    // time scale
    float t = iTime;
    t *= 1.75;

    // squaring creates snapping effect
    float ft = fract(t) * fract(t);

    float i = mod(floor(t), 12.0);
    float j = mod(floor(t) + 1., 12.0);


    float ao = (TWO_PI / 12.) * i;
    float aon = (TWO_PI / 12.) * j;

    // translate uv
    uv.x += mix(sin(ao), sin(aon), ft) * 0.175;
    uv.y += mix(cos(ao), cos(aon), ft) * 0.175;


    float a = ft * PI / 3.;
    mat2 rotation = mat2(cos(a), sin(a), -sin(a), cos(a));
    uv.xy *= rotation;

    float hs = smoothstep(0.105, 0.1, sdHexagon(uv, 0.15, -0.1));
    col = max(col, hs);

    // brighten
    col += 0.2;

    // Output to screen
    fragColor = vec4(col,1.0);
}
