vec2 triangle_wave(vec2 a){
    return abs(fract((a+vec2(1.,0.5))*1.5)-.5);
}

//----------------------------------------------------------------------------------------
//  1 out, 2 in...
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 8.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    float time1 = iTime/64./2.;
    uv += vec2(time1/2.0,time1/3.0)/t1/4.0+(vec2(cos(time1),sin(time1)))*8./t1;
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    vec2 t3 = vec2(0.);
    for(int k = 0; k < 9; k++){
        float p1 = sign(uv.x);
        //float random_num = hash12(floor(uv));
        uv += t2;
        uv /= scale;
        //uv.x += sign(uv.x)/2.;
        //uv.y += sign(uv.y)/2.;
        //scale -= col.x/4.*random_num;
        t2 = -p1*triangle_wave(uv-.5);
        t3 = p1*triangle_wave(uv.yx);

        uv = t2-t3;
        col.x = max(uv.y-uv.x*p1-col.x*(2.+uv.x),col.x)/sqrt(2.);
        col = abs(col.yzx-vec3(1.-col.x));
    }
    fragColor = vec4(col,1.0);
}
