#define T iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5* iResolution.xy) / iResolution.y;

    // Tile Parameters    
    vec2 gridUV = fract(uv * 10.);
    float id = uv.x * uv.y;

    // Rotation Parameters
    float speed = 4.;
    float phase = speed * T + id * 4.;

    gridUV.x -= (.5 + sin(phase) * .4);
    gridUV.y -= (.5 + cos(phase) * .4);

    float colorComponent = smoothstep(.105, .1, length(gridUV));

    vec4 colorTex = texture(iChannel0, uv);
    vec4 colorTex2 = texture(iChannel1, uv);

    colorTex = mix(colorTex, colorTex2, .5 + sin(iTime*2.) * .5);

    vec3 col = vec3(colorComponent + gridUV.x, colorComponent + gridUV.y, 0);



    col = mix(col, colorTex.xyz, abs(sin(T)));
    fragColor = vec4(col,1.0);
}
