 bool circle(vec2 p, vec2 o, float r) {//рисуем круг(точка для проверки на принадлежность,центр,радиус круга)
     float kY = iResolution.y / iResolution.x;//поправичка для оси Y ,так как у нас прямоугольный экран 
      return (p.x - o.x) * (p.x - o.x) + (p.y - o.y) * (p.y - o.y)*( kY*kY) < r*r; 
    }
 bool triangle(vec2 v, vec2 a,float s,float g)//точка вершины треугольника,точка для проверки,k(сторона треугольника имеет вид  y=kx),высота треугольника
{
    float x = v.x - a.x,
          y = v.y - a.y;
    if (y>x*s&&y>x*(-s)&&y<g) {
      return true;
    }else return false;
}
bool rectangle(vec2 v ,float a,float b,float c,float d)
{
if(v.x>a&&v.x<b&&v.y>c&&v.y<d){
