#define STEPS 1028

const vec2 zoom = vec2(.251, 0.00005);

const int numColors = 6;

// Colors steps to blend between.
const vec3[numColors] colors = vec3[numColors](
  vec3(1.,0.,0.),
  vec3(1.,0.,1.),
  vec3(0.,0.,1.),
  vec3(0.,1.,1.),
  vec3(0.,1.,0.),
  vec3(1.,1.,0.)
);

vec2 sqr(vec2 z) {
  return vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord/iResolution.xy;
    uv = 2.*uv - 1.;
    uv.x *= iResolution.x / iResolution.y;

    float time = mod(iTime/2., 23.5);
    float zoomFactor = pow(2., time);
    uv /= zoomFactor;

    vec2 z = vec2(0.);
    vec2 c = uv + zoom;

    int steps = 0;
    for (; dot(z,z) < 4. && steps < STEPS; ++steps) {
      z = sqr(z) + c;
    }

    vec3 col = vec3(0.);
    if (steps < STEPS) {
      float index = mod(log2(float(steps)), float(numColors));
      col = mix(
              colors[int(index)],
              colors[int(mod(index+1., float(numColors)))],
              fract(index));
    }

    // Fade in the transition to the start
    if (time < 2.) {
      col = mix(vec3(0.), col, time/2.);
    }

    // Output to screen
    fragColor = vec4(col, 1.);
}
