// Plot a line on Y using a value between 0.0-1.0
float plot(vec2 st,float pct) {    
    return smoothstep( pct-0.02, pct, st.y) - smoothstep( pct, pct+0.02, st.y); }

float plotx(vec2 st,float pct) {    
    return (-step(0.6 ,st.y) + step(0.5 ,st.y)) * (smoothstep( pct-0.01, pct, st.x) - smoothstep( pct, pct+0.01, st.x)); }



float vertical(vec2 st) {    
    return smoothstep( 0.5, 0.52, 0.5); }


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 st = fragCoord.xy/iResolution.xy;

    float x = st.x;
  //  x = (x -3.0)*2.0;
    float y = mod(x,0.1)+0.5;


   //float y = 0.5;

    //float y = smoothstep( 0.5, 0.52, x);;


    vec3 color = vec3(0);

    // Plot a line    
    float pct = plot(st,y);
    pct = max(pct,plotx(st,round(x/0.1)/10.0));

    color = (1.0-pct)*color+pct*vec3(0.0,1.0,0.0);

	fragColor = vec4(color,1.0);
}
