renderpass.code:
vec4 C1 = .5 - .25*vec4(0,0,1,0),     // dark color  ( seen black or gold )
     C2 = .5 + .25*vec4(0,0,1,1);     // clear color ( seen blue or white )

void mainImage( out vec4 O,  vec2 u )
{
    vec2 R = iResolution.xy,
         U = (2.*u - R ) / R.y;

    O = fract(2.*U.y) < .5 ? C1 : C2; // strips

    if ( U.x < -.7 ) O -= C1;         // subtract ambiant = dark color
 // if ( U.x >  .7 ) O += C1;  
    if ( U.x >  .7 ) O /= C2;         // undo lighting = clear color

 // if ( U.x < -1.6 ) O = .5+C1;      // back-transform how grey bg would appear in photo
 // if ( U.x >  1.6 ) O = .5*C2;      // back-transform how grey bg would appear in photo
 // if ( U.x < -1.6 ) O = C1;         // back-transform how black bg would appear in photo
 // if ( U.x >  1.6 ) O = C2;         // back-transform how white bg would appear in photo
}
