// https://www.shadertoy.com/view/3dGBzd

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,d=normalize(vec3((C-.5*r.xy)/r.y,1.));
    for(float i=0.,n,g=0.,t=iTime;i++<80.;O+=.01/exp(i*i*n))
    p=d*g-i/1e4,
        p.z-=2.5,
        p=R(p,vec3(.577),t),
        n=(cos(t)*.5+.5)*7.+1.,
        p*=n,
        p=abs(abs(abs(p)-clamp(n-4.,0.,4.))-clamp(n-2.,0.,2.))-clamp(n-1.,0.,1.),
        g+=n=(length(p-clamp(p,-.5,.5))*.7-.1)/n*.6;
}
