float random (float seed){
    return fract(sin(dot(vec2(seed),vec2(12.9898,78.233)))*43758.5453123);
}
float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 bg = vec3(0,0,0);
    vec3 col = vec3(0,0,0);
    float r1 = .032f;
    float r2 = .025f;
    float r3 = .3f;
    float PI = 3.1415f;
    float PI2 = PI * 2.f;
    float num_circles = 12.f;

    for (float i = 0.f; i < num_circles; i+=1.f) {

        float i_rad = map(i, 0.f, num_circles, -PI, PI);

        vec2 p = uv + r3 * vec2(cos(i_rad),sin(i_rad));

        if ( int(i) == int(fract(iTime / -2.) * num_circles)){
            if (length(p) < r1) {
                col = vec3(1,0,0);            
            }
        }
        else 
        {
            if (length(p) < r2) {
                col = vec3(1,1,1);
            }
        }

        col = max(col, bg);
    }

    fragColor = vec4(col,1.0);
}
