#define R3(p,a,t) mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a)
#define R2(p,t) (p*cos(t)+vec2(-p.y,p.x)*sin(t))
#define H(h) (cos((h)*6.3+vec3(0,23,21))*.5+.5)

void mainImage(out vec4 O, in vec2 C)
{
    O=vec4(0);
	vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));
    float i=0.,g=0.,e,s;
	for(;i++<90.;){
        p=d*g;
        p.z-=5.;
        p=R3(p,vec3(.577),iTime*.4);
        p-=i/2e4; //https://twitter.com/zozuar/status/1527091919511248897
        e=s=2.;
        for(int i=0;i<3;i++){
            p=abs(p)-.3;
            p.x<p.y?p=p.yxz:p;
            p.x<p.z?p=p.zyx:p;
            p.y<p.z?p=p.xzy:p;
            p.xy=abs(p.xy)-.2;
            p.yz=R2(p.yz,-.4);
            p*=2.;
            s*=2.;
        }
        p/=s;
        g+=e=(length(vec2(length(p.xy)-.6,p.z))-.03);
        O+=.015/exp(i*i*e);
    }
    O.rgb*=mix(vec3(1),H(p.y*p.x*2.),.6);
    O*=O*O;    
}
