#define MULT 1.5

float getMirroredFFT(vec2 uv)
{
    if (uv.x < 0.5) return texture(iChannel0, vec2(0.5 - uv.x, uv.y)).x;
    return texture(iChannel0, uv - vec2(0.5, 0.0)).x;
}

float getOffset()
{
    float offset = 0.0;

    for (int i = 0; i < 512; i++)
    {
        offset += texture(iChannel0, vec2(float(i) / 512.0, 0.0)).x * MULT;
    }

    offset /= 512.0;

    return offset;
}

vec4 line(vec2 pos)
{

    float offset = getOffset();

    vec2 uv = pos / iResolution.xy;

    float v = 0.5 + cos(pos.x / (0.2 * iResolution.x) + pos.y * 0.005 + iTime * 10.0 + 20.0 * offset);
    float fft = getMirroredFFT(uv) * MULT;

    float line_width = 100.0 * (offset - 0.3);
    float line_distance = 200.0 + 100.0 * sin(iTime * 0.1);

    if (int((offset + fft * 2.0) * 100.0 + pos.y + cos(iTime * 2.0) * 100.0) % int(line_distance) < int(line_width))
    {
        //Basic Color
        vec4 colBase = vec4(v, cos(iTime + offset), sin(iTime * 0.3), 1.0);
        //Detail Color
        vec4 colWave = vec4(sin(iTime + offset), cos(iTime * 0.3), v * fft, 1.0);
        return colBase + colWave;
    }
    return vec4(0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = line(fragCoord);
}
