void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.y = 1.0 - uv.y;

    uv.x /= iResolution.y/iResolution.x;

    uv.x = uv.x - 0.4;

    //position ALS UV KOORDINATEN NUTZEN UND VERZERREN
    //NICHT uv BENUTZEN!!!
    vec2 position = uv;



    vec3 col = vec3(position, 0.0);

    if(uv.x > 1.0 || uv.x < 0.0){
        col = vec3(0);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
