void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = SI(fragCoord);
}

vec2 T(float x, float y)
{
    return vec2(x*x - y*y, 2.*x*y)*0.0001 + vec2(x,y);
}

void mainImage( out vec4 fragColor, in vec2 fgi )
{
    fgi -= iResolution.xy*0.5;
    vec2 tfgi = T(fgi.x, fgi.y);

    fgi += iResolution.xy*0.5;
    tfgi += iResolution.xy*0.5;
    fragColor = vec4(SI(tfgi).x == 1. ? RCooling : SI(fgi).x);
}

float Rand(float t)
{
    return fract(600.*(sin(mod(t, 50000.))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = distance(iMouse.xy, fragCoord) < 5. || Rand(fragCoord.x * fragCoord.y + iTime + iDate.w)-0.999 > 0.0008? vec4(1.) : SI(fragCoord)*Cooling;
}
