#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

float ang(vec2 uv) {return atan(uv.y / uv.x); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float scale = 40.;
    float segments = 10.;

    uv += vec2(cos(iTime + tan(uv.x)), sin(iTime + tan(uv.y))) * 0.3; 

    uv *= rot(iTime * 0.1 * round(distance(uv, vec2(0,0)) * scale));

    float bright = smoothstep(0., 1.,cos(distance(uv, vec2(0,0)) * scale * 6.25));

    float dark = sin(ang(uv) * segments) * 3.;

    bright *= dark;

    vec4 colcentre = vec4(0.282,0.020,0.380,0.);
    vec4 colouter = vec4(0.161,0.318,0.878,0.);

    vec4 col = mix(colcentre, colouter, distance(uv, vec2(0,0))) * bright;

    if(bright <= 0.8) col = vec4(0.016,0.020,0.204,0.);

    fragColor = vec4(col);
}
