void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Variables
    float indirect_intensity = 0.1;
    vec2 indirect_scale = vec2(0.07, 0.02);


    vec2 xy = fragCoord.xy / iResolution.xy;//UV Coordinates for Main Texture
    vec2 xy_2 = fragCoord.xy / iResolution.xy;//UV Coordinates for Indirect Texture
    xy_2 = xy_2 + vec2(-iTime / 4.0,0.0);//Moving the Indirect Texture across the image
    xy_2 = vec2(xy_2.x * indirect_scale.x, xy_2.y * indirect_scale.y);// Setting which way the Indirect Texture must warp the image.
    vec4 texColor_2 = texture(iChannel1,xy_2);//Getting the Indirect Texture and applying its UV Coordinates
    xy = xy + vec2(texColor_2.r * indirect_intensity, texColor_2.r * indirect_intensity);//Indirect Texture Intensity
    vec4 texColor = texture(iChannel0,xy);//Getting the Main Texture and applying its UV Coordinates
    fragColor = texColor;//Application
}
