void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = T(ivec2(fragCoord)).wwww;
}

void ConfigureOwnBall(inout vec4 myBall)
{
    //Get all variables
    ivec2 dir = GetTexelDirection(myBall);

    //Look if it will lose its ball
    myBall *= WillLandInThisCell(dir);
}

void ConfigureNeighbourBall(inout vec4 myBall, in ivec2 RelativePositionToNeighbour, in ivec2 p)
{
    //Get all variables of neighbourball
    vec4 NeighbourBall = T(p - RelativePositionToNeighbour);
    ivec2 dirN = GetTexelDirection(NeighbourBall);

    //Look if will get neighbour ball
    NeighbourBall *= WillLandInThisCell(dirN - RelativePositionToNeighbour);

    //Apply physics to the neighbour ball(this is not accurate)
    //NeighbourBall.xy -= min(GetSpeed(NeighbourBall), 1.) * GetDirection(NeighbourBall) * iTimeDelta * GetWeight(NeighbourBall);

    //Get neighbour ball if landing on own cell
    myBall += NeighbourBall;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Get start variables
    ivec2 p = ivec2(fragCoord);
    vec4 myBall = T(p);

    //Look if myBall will lose its ball
    ConfigureOwnBall(myBall);

    //Look around at neightbours balls if you can inherit one
    ConfigureNeighbourBall(myBall, ivec2(-1., -1.), p);
    ConfigureNeighbourBall(myBall, ivec2(-0., -1.), p);
    ConfigureNeighbourBall(myBall, ivec2( 1., -1.), p);
    ConfigureNeighbourBall(myBall, ivec2(-1., -0.), p);
    //Center
    ConfigureNeighbourBall(myBall, ivec2( 1., -0.), p);
    ConfigureNeighbourBall(myBall, ivec2(-1.,  1.), p);
    ConfigureNeighbourBall(myBall, ivec2(-0.,  1.), p);
    ConfigureNeighbourBall(myBall, ivec2( 1.,  1.), p);

    //Return the ball to this cell
    fragColor = myBall;
}

vec4 GetStarterBalls(in vec2 fragCoord)
{
    vec4 ball = texture(iChannel1, fragCoord/iResolution.xy);

    ball.xy = ball.xy - 0.5;

    ball.xy *= 7.;

    ball.w = ceil(ball.z-0.5);

    ball.w = clamp(round(ball.w), 0., 1.);

    return ball;
}

bool AtBorder(in ivec2 p)
{
    return p.x < 2 || p.y < 2 || p.x > int(iResolution.x) - 3 || p.y > int(iResolution.y) - 3;
}

vec4 GetBorderBalls(in ivec2 p)
{
    return vec4(0.);
}

void ApplyGravity(inout vec4 myBall)
{
    myBall.y += GetWeight(myBall) * -9.81;
}

void ApplyCollission(inout vec4 myBall, in ivec2 p)
{
    //The best option is to look around and compute how much speed it gets from its neighbours and loses from its neighbours

    //Get variables
    ivec2 dir = GetTexelDirection(myBall);
    vec4 colBall = T(p + dir);

    myBall.xy *= isBall(myBall) && dir != ivec2(0) && isBall(colBall) ? -1. : 1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    //Get start variables
    ivec2 p = ivec2(fragCoord);
    vec4 myBall = T(p);

    //Add in some balls
    myBall += iFrame < 10 && !isBall(myBall) ? GetStarterBalls(fragCoord) : vec4(0.);

    //Add the border balls
    myBall = AtBorder(p) ? vec4(0.,0.,0.,1.) : myBall;

    //Apply physics to ball
    ApplyGravity(myBall);

    //Apply collission to ball
    ApplyCollission(myBall, p);

    //Return the ball to this cell
    fragColor = myBall;
}

#define T(fragCoord) texelFetch(iChannel0, fragCoord, 0)

ivec2 GetTexelDirection(in vec4 ball)
{
    return ivec2(clamp(ball.xy, -1., 1.));
}

vec2 GetDirection(in vec4 ball)
{
    return ball.x + ball.y == 0. ? vec2(0.) : normalize(ball.xy);
}

vec2 GetImpulse(in vec4 ball)
{
    return ball.xy;
}

float GetSpeed(in vec4 ball)
{
    return length(ball.xy);
}

float GetWeight(in vec4 ball)
{
    return ball.z;
}

bool isBall(in vec4 ball)
{
    return ball.w >= 1.;
}

float GetBall(in vec4 ball)
{
    return ball.w;//Get this value and multiply this with the force, but this can also be checked with mass
}

float WillLandInThisCell(in ivec2 pos)
{
    return pos == ivec2(0) ? 1. : 0.;
}

float WillLandInThisCell(in vec2 pos)
{
    return pos == vec2(0.) ? 1. : 0.;
}
