#define MAX_X 3.14159
#define MAX_Y 1.5

float pixelHeight() {
    return (2. * MAX_Y) / iResolution.y;
}

float pixelWidth() {
    return (2. * MAX_X) / iResolution.x;
}

float eps() {
    return min(pixelHeight(), pixelWidth());
}

float near(float a, float b) {
    float diff = abs(a - b);
    return smoothstep(5.*eps(), 0., diff);
}

float wave(float x) {
    return cos(5. * x) * exp(-abs(x));
}

void mainImage(out vec4 rgba, in vec2 xy) {
    vec2 uv = xy - 0.5 * iResolution.xy;
    uv.x *= pixelWidth();
    uv.y *= pixelHeight();

    float a = texture(iChannel0, vec2(iTime, 0.75)).x - .5;
    float f = a*wave(uv.x);
    vec3 col = vec3(0., 1., 1.) * vec3(near(f, uv.y));

    rgba = vec4(col,1.0);
}
