const float PI = 3.141592653;
const int CUFFS = 36;

const vec2 sig = vec2(-1, 1);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    float c = 0.0;
    for (int i = 0; i < CUFFS; i++) {
        float t = 2.0 * PI * float(i) / float(CUFFS) + 0.1 * iTime * sig[i % 2];
        vec2 tuv = clamp(mat2(cos(t), sin(t), -sin(t), cos(t)) * uv, -1.0, 1.0);
        vec2 tCoord = (tuv * iResolution.y + iResolution.xy) / 2.0;
        c = max(c, texture(iChannel0, tCoord / iResolution.xy).x);
    }
    fragColor = vec4(c, c, c, 1);
}

// Copyright © 2021 IWBTShyGuy
// Attribution 4.0 International (CC BY 4.0)

const float X_VELO = 0.5;
const float Y_VELO = 0.7;

bool isKeyDown(int code) {
    return texelFetch(iChannel1, ivec2(code, 0), 0).x > 0.0;
}
const int Key_R = 82;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    if (iFrame == 0 || isKeyDown(Key_R)) {
        fragColor = vec4(0, 0, 0, 1);
        return;
    }
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec2 point = vec2(cos(X_VELO * iTime), sin(Y_VELO * iTime)) * 0.7;
    float c = max(sign(0.002 - distance(uv, point)), 0.0);
    c = max(c, texture(iChannel0, fragCoord / iResolution.xy).x);
    fragColor = vec4(c, c, c, 1);
}
