void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x;
    uv = uv - vec2(0,(iResolution.y-iResolution.x)/iResolution.x)/2.;
    float n = 0.05+iMouse.x/2000.;
    vec2 B = vec2(mod(uv.x,n)/n,mod(uv.y,n)/n);
    vec2 A = 2.*(uv-B*n+vec2(n/2.,n/2.)-.5);
    vec3 col = .5*vec3(A.x,A.y,mod(F(A.x,A.y),n))+.5;

    B = 2.*B-1.;

    float a = atan(F(A.x,A.y));

    vec2 ang = length(B)*vec2(cos(a),sin(a));
    if (length(B)>.5){

    if (length( B - ang) < .5-length(B)/2.){

    col = vec3(0,0,0);}}

    else if (length( B - ang) < length(B)/5.){

    col = vec3(0,0,0);}


    // Output to screen
    fragColor = vec4(col,1.0);
}

float F( float a, float b )
{
    return tan(-3.1415/2.+atan(b/a));
}
