void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    float movement = 0.05 * sin(uv.y * 13.0 + 1.7 * iTime) +
                     0.13 * sin(uv.y * 17.0 + 1.6 * iTime);
    uv.x += movement * pow(uv.y,3.76);
    uv.x *= 20.;
    uv.x = fract(uv.x);



    float t = smoothstep(0.1, 0.4, uv.x) -
                  smoothstep(0.5, 0.9, uv.x);

    vec4 color1 = vec4(0., 1., 1. , 1.);        
    vec4 color2 = vec4(1.,0.,1., 1.);     

    vec4 color = mix(color1, color2, t);

    fragColor = color;


}
