const float HEIGHT=1.;
/*------------OBJ DEFINE------------*/
const int OBJ_NUM=2;
const int ID_BOUND=0;
const int ID_S=1;
MarchPoint sdf(in vec3 p)
{
    float[OBJ_NUM] objSDF;
    for(int i=0;i<OBJ_NUM;i++)
        objSDF[i]=INF;
    objSDF[ID_BOUND]=-sdSphere(p-vec3(0,0,0),MAX_DIST);//Bound


    /*--------add objects begin--------*/
    objSDF[ID_S]=sdSphere(p,.4);
    //objSDF[ID_S]=sdCube(p,vec3(.6));
    /*--------add objects end--------*/


    MarchPoint tar;tar.d=objSDF[ID_BOUND];
    for(int i=1;i<OBJ_NUM;i++)
        if(tar.d>objSDF[i])
            tar.d=objSDF[i],
            tar.id=i;
    return tar;
}
vec3 getNormal(in vec3 p)
{
    vec2 e=vec2(0,EPS);
    float c=sdf(p).d;
    return normalize(vec3(sdf(p+e.yxx).d-c,
                          sdf(p+e.xyx).d-c,
                          sdf(p+e.xxy).d-c));
}
struct FragInfo
{
    vec3 worldPos,normal,kc;
};
/*-------------exp code-------------*/
//texcube is from iq:
//https://www.shadertoy.com/view/ldSGzR
vec4 texCube( sampler2D sam, in vec3 p, in vec3 n )
{
	vec4 x = texture( sam, p.yz );
	vec4 y = texture( sam, p.zx );
	vec4 z = texture( sam, p.xy );
	return x*abs(n.x) + y*abs(n.y) + z*abs(n.z);
}

vec3 shadeSphere(in FragInfo I)
{
    I.kc=texCube(iChannel2,I.worldPos*2.,I.normal).xyz;
    float height=HEIGHT*texCube(iChannel1,I.worldPos*1.,I.normal).x;
    vec3 nPos=I.worldPos+.2*height*I.normal;
    vec3 tagent=dFdx(nPos),
         bitagent=dFdy(nPos);

    I.worldPos=nPos;
    I.normal=normalize(-cross(tagent,bitagent));

    vec3 lightPos=cam.pos+vec3(0,.5,0);
    vec3 pl=lightPos-I.worldPos;
    vec3 ld;
    float plLen=length(pl);
    ld=pl/plLen;
    vec3 diff=vec3(1)*max(dot(ld,I.normal),0.)/(plLen*plLen)*.5;
    vec3 amb=vec3(.2);
    return I.kc*(diff+amb);
}
/*-------------exp code end-------------*/
MarchHit rayCast(in Ray r)
{
    float d=0.,i=0.,l=0.;
    MarchPoint point;
    for(;i<MAX_STEP;i++)
    {
        point=sdf(r.o+d*r.d);
        if(abs(point.d)<MIN_DIST)
            break;
        l=i/MAX_STEP;
        d+=point.d*((1.-l*l*l)*0.7+0.3);
        if(point.id==ID_BOUND)
        {
            d=MAX_DIST;
            break;
        }
    }
    return MarchHit(d,i,point.id);
}
vec4 render(in Ray r)
{
    MarchHit h=rayCast(r);

    vec3 pos=r.o+r.d*h.d,nml=getNormal(pos);
    vec3 col=vec3(0);
    float quickAO=h.marchTimes/MAX_STEP;

    switch (h.id)
    {
        case ID_BOUND:
//Note:
//When I tried to turn on the skyBox
//the sphere texture will be wrong, 
//I'cant tell the reason.
            //col=texture(iChannel0,normalize(pos)).xyz;
            break;
        case ID_S:
            col=shadeSphere(FragInfo(pos,nml,vec3(1)));
            break;
    }
    return vec4(col,quickAO);
}
void mainImage( out vec4 O, in vec2 C )
{
    float t=iT,ct=mod(t,2.),rt=step(1.,ct)*(ct-1.)+floor(t*.5);
    cam.pos=vec3(0,0,0)+vec3(cos(rt),0,sin(rt))*1.;
    cam.dir=normalize(-cam.pos);
    cam.pos-=cam.dir*((1.-floor(ct))*sin(fract(t)*2.*PI)*.3+.2);

    cam.r=-cross(cam.dir,vec3(0,1,0));
    cam.u=-cross(cam.r,cam.dir);
    vec2 uv=(C-iR*.5)/iR.y;
    Ray r=Ray(cam.pos,normalize(uv.x*cam.r+uv.y*cam.u+1.*cam.dir));
    O=render(r);
#ifdef SHOW_RAYMARCH_STEPS
    O.rgb=mix(vec3(0,1,0),vec3(1,0,0),O.w);
#endif
}

#define iR iResolution.xy
#define iT iTime
#define iM iMouse


/*------------Env Settings------------*/
// #define SHOW_RAYMARCH_STEPS
/*------------Env Settings------------*/
const float MAX_DIST=20.;
const float MIN_DIST=1e-4;
const float MAX_STEP=50.;
const float EPS=1e-3;
const float PI=3.14159265;
const float DTA=PI/180.;
const float INF=1e20;
struct Ray
{
    vec3 o,d;
};
struct Cam
{
    vec3 pos,dir;
    vec3 r,u;
}cam;
struct MarchPoint
{
    float d;
    int id;
};
struct MarchHit
{
    float d,marchTimes;
    int id;
};
mat2x2 getRotMat(float r)
{
    r*=DTA;
    float c=cos(r),s=sin(r);
    return mat2x2(c,s,-s,c);
}
float sdCube(vec3 p,vec3 size)
{
    vec3 np=abs(p)-size*.5;
    float o=length(max(np,vec3(0)));
    float i=min(0.,max(np.x,max(np.y,np.z)));
    return o+i;
}
float sdSphere(vec3 p,float s)
{
    return length(p)-s;
}
