float eyeCircle(vec2 uv, vec2 center, float rad)
{
        float r=distance(uv,center);
        r = smoothstep(rad, rad +0.01, r); 
        return r; 
}

vec3 eyes(vec2 uv, vec2 mouse, vec3 currentCol, vec2 pos)
{
    vec3 ecol = mix(vec3(0.5),currentCol,eyeCircle(uv, pos, 0.3));
    ecol = mix(ecol,vec3(0.9),smoothstep(0.3,0.05,distance(pos,uv)));
    vec2 follow = normalize(mouse-pos);
    ecol =  mix(vec3(0.1),ecol,eyeCircle(uv, pos+follow*0.15, 0.15));
    ecol =  mix(vec3(0.9,0.2,0.3),ecol,eyeCircle(uv, pos+follow*0.15, 0.05));
    ecol =  mix(vec3(0.99),ecol,eyeCircle(uv-vec2(0.07), pos+follow*0.15, 0.05*(follow.x+0.5)/2.));
    return ecol;    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scaler = 6.;
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y*scaler;
    vec2 mouse = (iMouse.xy - 0.5 * iResolution.xy)/iResolution.y*scaler;

    vec3 col = vec3(0.);    

    /*for(float i = -10.; i <= 10.;i++)
    {
        for(float j = -10.; j <= 10.; j++)
        {
            col = eyes(uv, mouse, col, vec2(1.3*i,1.3*j));
        }
    }*/
    col = eyes(uv, mouse, col, round(uv));
    col = mix(col, vec3(1.,1.,1.), smoothstep(0.7,0.1,distance(mouse, uv)));
    fragColor = vec4(col,1.0);
}
