void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*acos(cos(atan(iTime/1.1))))/iResolution.xy*acos(cos(atan(iTime/1.1)));

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*sin(cos(tan(iTime*0.01+uv.xyx+vec3(0.1,0.2,0.4))));

    // Output to screen
    fragColor = vec4(col,1.0);
}
