void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 cd;
    cd.x = fragCoord.x - iResolution.x*0.5;
    cd.y = fragCoord.y - iResolution.y*0.7;
    cd = cd / iResolution.x;

    float pi = radians(180.);

    float fi = atan(cd.x/cd.y);
    if (cd.y < 0.){
        fi += pi;
    }
    fi = mod( fi + 2.*pi, 2.*pi );

    float r = pow( (cd.x*cd.x + cd.y*cd.y), 0.5 );

    float bps = 0.7;
    float sinx = 2.*pi*iTime*bps;

    float heartbeat = 0.7 + 0.3 * abs( sin(sinx) * sin(2.*sinx) ) * 0.5;

    //float heartbeat;
    //if (mod(iTime, 2.) > 1.){
    //    heartbeat = 0.7 + 0.3 * sin(sinx);
    //}else{
    //    heartbeat = 0.7 + 0.15 * sin(sinx);
    //}

    float r00 = .2;
    //float r0 = r00 * heartbeat * pow( sin(fi/2.), 0.5 );
    float r0 = r00 * heartbeat * sin(fi/2.);

    float rrel = r/r0;



    float red = 1. - 0.5*rrel;

    fragColor = vec4(red, 0, 0,1.0);
}
