void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord.xy / iResolution.xy;
    vec3[3] col3 = vec3[](
        vec3(1.0, 0.0, 0.0),
        vec3(0.0, 0.0, 1.0),
        vec3(0.0, 1.0, 0.0)
        );
    pos.x *= 2.0;
    int ind = int(pos.x);
    vec3 col = mix(col3[ind], col3[(ind + 1) % 3], fract(pos.x));
    fragColor = vec4(col, 1.0);
}
