void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);

    float sz = max(iResolution.x, iResolution.y);
    float scale = ceil(sz * pow(sin(iTime) * .5 + .5, 2.));
    ivec2 fc = ivec2(fragCoord / scale);

    if(((fc.x + fc.y) % 2) == 0)
        col = vec3(1, 1, 0);
    else
        col = vec3(0, 0, 1);

    fragColor = vec4(col, 1.0);
}
