float node(vec2 uv, vec2 pos, float r){
    uv.x = abs(uv.x);
    uv/=r;
    float c = (uv.y<pos.y?step(dot(uv-pos-vec2(0.,.45),normalize(vec2(1.,-1.61803398875))),1.):
            step(dot(uv-pos,normalize(vec2(1.61803398875,1.))),1.)) 
            - 
            (uv.y<pos.y?step(dot(uv-pos-vec2(0.,.45),normalize(vec2(1.,-1.61803398875))),1.-0.05):
            step(dot(uv-pos,normalize(vec2(1.61803398875,1.))),1.-0.05))
            ;

    return c;
}

mat2 rotate2d(float _angle){
        return mat2(cos(_angle),sin(_angle),
                    -sin(_angle),cos(_angle));
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy)/
        (iResolution.y < iResolution.x ? iResolution.y : iResolution.x);

    float col = 0.;  
    float scale = 10.;
    vec3 res = vec3(0.);
    uv*=scale;

    float foldNum = 12.;
    for (float i=0.;i<foldNum;i+=1.){
        uv = rotate2d(2.*3.1415/foldNum) *uv;
        float var = 1.+0.5*(sin(-2.*iTime+i*2.*3.1415/foldNum)+1.)/4.;
        col = node(uv, vec2(0.,2.), var);
        res.r = max(res.r, col*cos(iTime/10.));
        res.g = max(res.g, col*sin(iTime/10.));
        res.b = max(res.b, col * var);
    }


    fragColor = vec4(res,1.0);
}
