void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
         uv.x= uv.x*iResolution.x/iResolution.y;
    float leng = iResolution.x/iResolution.y/2.0;

    // Time varying pixel color
    float d = length((uv-vec2(leng,0.5))*30.0) + iTime+uv.x*20.0+uv.y*20.0;
    float c =smoothstep(0.3,0.99,abs(sin(d+0.5)));
    float k =step(0.8,abs(sin(d)))+c;
    // Output to screen
    fragColor = vec4(k,abs(cos(d)),1.0,1.0);
}
