float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdRoundedBox( in vec2 p, in vec2 b, in vec4 r )
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
    vec2 e0 = p1-p0, e1 = p2-p1, e2 = p0-p2;
    vec2 v0 = p -p0, v1 = p -p1, v2 = p -p2;
    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min(min(vec2(dot(pq0,pq0), s*(v0.x*e0.y-v0.y*e0.x)),
                     vec2(dot(pq1,pq1), s*(v1.x*e1.y-v1.y*e1.x))),
                     vec2(dot(pq2,pq2), s*(v2.x*e2.y-v2.y*e2.x)));
    return -sqrt(d.x)*sign(d.y);
}

float arrow(vec2 p, vec2 t, vec2 s){
    return sdTriangle((p) + t, vec2(.01, .0)*s ,  vec2(-.0, -.02)*s,  vec2(-.0, .02)*s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ar = vec2(iResolution.x/iResolution.y,1.0);
    vec2 p = (uv-.5)*ar;    
    vec3 c;

    c = vec3(0.80 - uv.x * 0.01);    
    c += texture(iChannel0,uv*5.*ar).r*0.05;    

    float t1 = 0.;
    float t2 = 5.;

    uv += cos((uv.x+uv.y)*5.0+iTime*3.0)*0.03 * smoothstep(4., 9.0, iTime) *  smoothstep(10.0, 7.0, iTime);

    uv-=vec2(.5,-.03 + smoothstep(0., 10.0, iTime));
    c = mix(c, vec3(0.941), smoothstep(0.001,.0, -.0+sdRoundedBox(uv, vec2(.5, .03) , vec4(0.,0.01, 0., 0.01))));

    uv-=vec2(.0, 2.0 * (1.0 - smoothstep(t1, t2, iTime)) );
    c = mix(c, vec3(0.1), smoothstep(0.001,.0, -.0 + arrow(uv,vec2(0.45,0.0), vec2(-1.0))));
    c = mix(c, vec3(0.1), smoothstep(0.001,.0, -.0+sdRoundedBox(uv+vec2(0.462,0.0), vec2(.0035, .02)/ar , vec4(0.))  ));

    uv-=vec2(.0, 2.0 * (1.0 - smoothstep(t1, t2, iTime)) );
    c = mix(c, vec3(0.1), smoothstep(0.001,.0, -.0 + arrow(uv,vec2(0.42,0.0), vec2(1.0))));

    uv-=vec2(.0, 2.0 * (1.0 - smoothstep(t1, t2, iTime)) );
    c = mix(c, vec3(0.1), smoothstep(0.001,.0, -.015 + length((uv-vec2(0.35,0.0))*ar)));

    uv-=vec2(.0, 2.0 * (1.0 - smoothstep(t1, t2, iTime)) );
    uv-=vec2(.4,0.);
    c = mix(c, vec3(0.1), smoothstep(0.001,.0, -.001 + arrow(uv,vec2(-0.02,0.0), vec2(-1.))));
    c = mix(c, vec3(0.1), smoothstep(0.001,.0, -.0+sdRoundedBox(uv-vec2(0.012,0.0), vec2(.005, .007) , vec4(0.))));
    c = mix(c, vec3(0.1), smoothstep(0.001,.0, -.0+sdRoundedBox(uv-vec2(0.002,0.0), vec2(.002, .007) , vec4(0.))));

    uv-=vec2(.0, 2.0 * (1.0 - smoothstep(t1, t2, iTime)) );
    uv-=vec2(.06,0.);
    float r = sdRoundedBox(uv, vec2(.02, .02)/ar , vec4(0.));
    r = max(r, -sdRoundedBox(uv, vec2(.01, .01)/ar , vec4(0.)));
    r = max(r, .002-abs(uv.x));
    r = max(r, .002-abs(uv.y));

    c = mix(c, vec3(0.1), smoothstep(0.001,.0, -.0+r));

    p -= vec2(0.0,  -1.+( smoothstep(5.0, 14.0, iTime)) );

    c = mix(c, vec3(0.1), smoothstep(0.008,.0, -.1+ length(p+vec2(0.03*cos(cos(iTime*4.321)), 0.03*cos(cos(iTime*5.125))))));
    c = mix(c, vec3(0.1), smoothstep(0.008,.0, -.03+ sdSegment(p,vec2(0.0),vec2(0.01*cos(iTime*4.321),-0.5))));
    c = mix(c, vec3(0.1), smoothstep(0.008,.0, -.03+ sdSegment(p,vec2(0.0,-0.2),vec2(cos(cos(iTime*4.))*-0.2,cos(iTime)*0.1-0.3))));
    c = mix(c, vec3(0.1), smoothstep(0.008,.0, -.03+ sdSegment(p,vec2(0.0,-0.2),vec2(cos(cos(iTime*5.))*-0.2,cos(iTime*2.)*0.1-0.3))));

    // Output to screen
    fragColor = vec4(c,1.0);
}
