void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x*=2.0*3.14;
    uv.y-=0.5;
    uv.y*=2.0;
    if(abs(uv.y-sin(uv.x-3.0*iTime))<0.01){
        fragColor=vec4(1.0,0.0,0.0,1.0);
    }else if(abs(uv.y-sin(uv.x-5.0*iTime))<0.01){
        fragColor=vec4(1.0,1.0,0.0,1.0);
    }
    else if(abs(uv.y-sin(uv.x-7.0*iTime))<0.01){
        fragColor=vec4(0.4,0.6,1.0,1.0);
    }
    else if(abs(uv.y-sin(uv.x-9.0*iTime))<0.01){
        fragColor=vec4(0.7,0.3,0.2,1.0);
    }
    else if(abs(uv.y-sin(uv.x-11.0*iTime))<0.01){
        fragColor=vec4(0.35,0.27,0.55,1.0);
    }
    else if(abs(uv.y)<0.005){
        fragColor=vec4(0.0,0.0,1.0,0.0);
    }
    else{
        fragColor=vec4(1.0);
    }
}
