#define three(p) t(A_,C_,p) + t(G_,I_,p) + t(M_,O_,p) + t(C_,O_,p)
#define four(p) t(N_,B_,p) + t(B_,G_,p) + t(G_,I_,p)

#define five(p) t(A_,C_,p) + t(A_,G_,p) + t(G_,I_,p) + t(I_,O_,p) + t(M_,O_,p)
#define six(p)  t(C_,A_,p) + t(A_,M_,p) + t(M_,O_,p) + t(O_,I_,p) + t(I_,G_,p)
#define seven(p) t(D_,A_,p) + t(A_,C_,p) + t(C_,O_,p) + t(H_,I_,p)
#define eight(p) t(A_,C_,p) + t(C_,O_,p) + t(O_,M_,p) + t(M_,A_,p) + t(G_,I_,p)
#define nine(p)  t(M_,O_,p) + t(O_,C_,p) + t(A_,C_,p) + t(A_,G_,p) + t(G_,I_,p)
#define zero(p) t(A_,C_,p) + t(C_,O_,p) + t(O_,M_,p) + t(M_,A_,p)
#define under(p) t(M_,O_,p)

vec2 caret_origin = vec2(1.2, .75);
vec2 caret;

//-----------------------------------------------------------------------------------
float minimum_distance(vec2 v, vec2 w, vec2 p)
{	// Return minimum distance between line segment vw and point p
  	float l2 = (v.x - w.x)*(v.x - w.x) + (v.y - w.y)*(v.y - w.y); //length_squared(v, w);  // i.e. |w-v|^2 -  avoid a sqrt
  	if (l2 == 0.0) {
		return distance(p, v);   // v == w case
	}

	// Consider the line extending the segment, parameterized as v + t (w - v).
  	// We find projection of point p onto the line.  It falls where t = [(p-v) . (w-v)] / |w-v|^2
  	float t = dot(p - v, w - v) / l2;
  	if(t < 0.0) {
		// Beyond the 'v' end of the segment
		return distance(p, v);
	} else if (t > 1.0) {
		return distance(p, w);  // Beyond the 'w' end of the segment
	}
  	vec2 projection = v + t * (w - v);  // Projection falls on the segment
	return distance(p, projection);
}

//-----------------------------------------------------------------------------------
float textColor(vec2 from, vec2 to, vec2 p)
{
	p *= font_size;
	float inkNess = 0., nearLine, corner;
	nearLine = minimum_distance(from,to,p); // basic distance from segment, thanks http://glsl.heroku.com/e#6140.0
	inkNess += smoothstep(0., 1., 1.- 14.*(nearLine - STROKEWIDTH)); // ugly still
	inkNess += smoothstep(0., 2.5, 1.- (nearLine  + 5. * STROKEWIDTH)); // glow
	return inkNess;
}

//-----------------------------------------------------------------------------------
vec2 grid(vec2 letterspace) 
{
	return ( vec2( (letterspace.x / 2.) * .65 , 1.0-((letterspace.y / 2.) * .95) ));
}

//-----------------------------------------------------------------------------------
float count = 0.0;
float gtime;
float t(vec2 from, vec2 to, vec2 p) 
{
	count++;
	if (count > gtime*20.0) return 0.0;
	return textColor(grid(from), grid(to), p);
}

//-----------------------------------------------------------------------------------
vec2 r()
{
	vec2 pos = coord.xy/iResolution.xy;
	pos.y -= caret.y;
	pos.x -= font_spacing*caret.x;
	return pos;
}

//-----------------------------------------------------------------------------------
void add()
{
	caret.x += .9;
}

//-----------------------------------------------------------------------------------
void space()
{
	caret.x += 1.5;
}

//-----------------------------------------------------------------------------------
void newline()
{
	caret.x = caret_origin.x;
	caret.y -= .18;
}

//-----------------------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = mod(iTime, 11.0);
    gtime = time;

	float d = 0.;
	//vec3 col = vec3(0.8, .07+0.07*(.5+sin(fragCoord.y*3.14159*1.1+time*2.0)) + sin(fragCoord.y*.01+time+2.5)*0.05, 0.0);
        vec3 col;

    coord = fragCoord;

	caret = caret_origin;

	// the quick brown fox jumps over the lazy dog...
	/*d += T(r()); add(); d += H(r()); add(); d += E(r()); space();
	d += Q(r()); add(); d += U(r()); add(); d += I(r()); add(); d += C(r()); add(); d += K(r()); space();
	d += B(r()); add(); d += R(r()); add(); d += O(r()); add(); d += W(r()); add(); d += N(r()); space();
	newline();
	d += F(r()); add(); d += O(r()); add(); d += X(r()); space();
	d += J(r()); add(); d += U(r()); add(); d += M(r()); add(); d += P(r()); add(); d += S(r()); space();
	d += O(r()); add(); d += V(r()); add(); d += E(r()); add(); d += R(r()); space();
	newline();
	d += T(r()); add(); d += H(r()); add(); d += E(r()); space();
	d += L(r()); add(); d += A(r()); add(); d += Z(r()); add(); d += Y(r()); space();
	d += D(r()); add(); d += O(r()); add(); d += G(r()); add(); d += STOP(r()); add(); d += STOP(r()); add(); d += STOP(r());
    */
    space();space();space();space();space();space();
    d += Gu(r());    add(); space(); d += Pu(r()); add(); space(); d += Uu(r()); 
    newline();    newline();    newline(); space();
    d += six(r());  add(); d += STOP(r());  add(); d += four(r()); add(); d += STOP(r()); add();
    d += nine(r()); add(); d += under(r()); add(); d += R(r());    add(); d += E(r());    add();
    d += L(r());    add(); d += E(r());     add(); d += A(r());    add(); d += S(r());    add();
    d += E(r());    add(); d += under(r()); add(); d += two(r());  add(); d += zero(r()); add();
    d += two(r());  add(); d += two(r());  add(); d += zero(r());  add(); d += seven(r());  add();
    d += three(r()); add(); d += zero(r());   add();

	d = clamp(d* (.75+sin(fragCoord.x*PI*.5-time*4.3)*.5), 0.0, 1.0);

    col += vec3(d*.5, d, d*.85);
	vec2 xy = fragCoord.xy / iResolution.xy;
	col *= vec3(.1, .4, .3) + 0.5*pow(100.0*xy.x*xy.y*(1.0-xy.x)*(1.0-xy.y), .4 );	
    fragColor = vec4( col, 0.0 );
}
