#define R                           iResolution
#define TO_CENTERED_NDC(SC)       ( ((SC)+(SC)-R.xy) / R.y )         // in range x=[-AR; AR], y=[-1.0; 1.0]
#define NZMX                      ( (iMouse.x+iMouse.x-R.x) / R.x )  // in range [-1.0; 1.0]
#define NZMY                      ( (iMouse.y+iMouse.y-R.y) / R.y )  // in range [-1.0; 1.0]
#define LMB_IS_HOLD               ( iMouse.z > 0.0 )

float L1(vec2 B, vec2 E)
{
    vec2 D = E - B;
    return abs(D.x) + abs(D.y);
}

/* Solution from https://www.desmos.com/calculator/fo3y8ty7ne *
#define X(t) (x0 + (x1 - x0)*t)
#define Y(t) (y0 + (y1 - y0)*t)
float segmentSDF_L1(
    vec2 NDC,
    vec2 S,
    vec2 E)
{
    float y0 = S.y, y1 = E.y, x0 = S.x, x1 = E.x, a = NDC.x, b = NDC.y;

    float t0 = clamp((b+y0)/(y0-y1), 0.0, 1.0);
    float t1 = clamp((a-x0)/(x1-x0), 0.0, 1.0);
    float t2 = clamp((b-y0)/(y1-y0), 0.0, 1.0);

    vec2 C0 = vec2(X(t0), Y(t0));
    vec2 C1 = vec2(X(t1), Y(t1));
    vec2 C2 = vec2(X(t2), Y(t2));

    float R = sinOSC(0.0, 0.3, iTime);
    return min(L1(C0, NDC), min(L1(C1, NDC), L1(C2, NDC))) - R;
}
/* OR */
#define P(t) (S + (E - S)*t)
float segmentSDF_L1(
    vec2 NDC,
    vec2 S,
    vec2 E)
{
    float R = sinOSC(0.0, 0.3, iTime);
    float invD0 = 1.0 / (S.y-E.y);
    return min(L1(P(clamp((NDC.x-S.x) / (E.x-S.x), 0.0, 1.0)), NDC), 
               min(L1(P(clamp((NDC.y+S.y) * invD0, 0.0, 1.0)), NDC),
                   L1(P(clamp((S.y-NDC.y) * invD0, 0.0, 1.0)), NDC))) - R;
}
