/* https://www.shadertoy.com/view/ddfGz2 */
vec3 colorize_field(float dist, vec3 color)
{    
    float F = 30., freq = F*dist, aa = 1./F, C = 0.7, amp = 0.2, width = 0.3,
          waves_sdf = C + amp*smoothstep(8./R.y, 0., (width+cos(freq)) * aa),
          exp_raise = 1. - exp(-4.*abs(dist));

    color = 1.0 - sign(dist) * color; // Change color if dist is negative. 
    color *= exp_raise;               // Attenuate color around edges.     
    color *= waves_sdf;               // Add waves.

    return mix(color, vec3(1), smoothstep(8./R.y, 0., abs(dist)) );
}

vec3 add_metric_box(
    vec2 NDC,
    vec2 S,
    vec2 E,
    vec3 color)
{
    float unit = 7.0 / R.y;

    vec2 M = 
        LMB_IS_HOLD ?
            TO_CENTERED_NDC(iMouse.xy) * 2.0 :
            rotateAroundZ(vec2(1, 0), iTime * 0.5) * 1.5;
            //vec2(0);

    // Add point
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(NDC - M) - 0.008));

    float dist_form_point_to_box_edge = L1(NDC, M) - abs(segmentSDF_L1(M, S, E));
    float outline = abs(dist_form_point_to_box_edge);

    // Add metric box
    return mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, outline));
}

void mainImage( out vec4 O, in vec2 SC )
{
    float offs = sinOSC(-0.5, 0.5, iTime * 0.5);
    vec2 NDC = TO_CENTERED_NDC(SC) * 2.0;

    vec2 S = vec2(-1.0, -1.0) * 0.65; 
    vec2 E = vec2(1.0, 1.0) * 0.65;

    S = rotateAroundZ(S, iTime * 0.1) + vec2(offs, -offs);
    E = rotateAroundZ(E, iTime * 0.1) + vec2(-offs, offs);

    //S = vec2(0.0, 0.65);
    //E = vec2(0.0, -0.65);

    float minDist = segmentSDF_L1(NDC, S, E);

    // Blending.
    vec3 color = colorize_field(minDist, vec3(0.1, 0.4, 0.7));
    color = add_metric_box(NDC, S, E, color);

    O = vec4(color, 1.0);
}

/* Returns sine modulated value in range [min, max]. */
float sinOSC(in float min, in float max, in float T)
{
    float v = (sin(T) + 1.0) * 0.5; // map T to [0.0, 1.0];
    return min + v * (max - min);   // map T to [min, max];
}

vec2 rotateAroundZ(vec2 p, float a)
{
    float s = sin(a);
    float c = cos(a);

    return vec2(
        p.x * c - p.y * s,
        p.y * c + p.x * s);
}
