float drawCircle(vec2 dot, vec2 center, float radius, float blur){
    float len = length(dot - center);

    return smoothstep(radius, radius - blur, len);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;


    float mask = drawCircle(uv,vec2(0,0),.4,0.02);


    mask -= drawCircle(uv, vec2(0,0),0.3, 0.02);

    mask += drawCircle(uv, vec2(0,.08),0.3, 0.03);

    float right_eye = drawCircle(uv, vec2(0.1,0.1),0.05, 0.01);
    float left_eye = drawCircle(uv, vec2(-0.1,0.1),0.05, 0.01);

    mask -= right_eye;

    mask -= left_eye;

    vec3 col = vec3(100.0,.0,.0) * mask;



    // Output to screen
    fragColor = vec4(col,1.0);
}
