#define MAX_HEIGHT 0.4
#define SPEED 20.0
#define FREQ 1.2
#define THICKNESS 0.04
#define BLOOM 1.1


float beam(vec2 uv, float max_height, float offset, float speed, float freq, float thickness) {
	uv.y -= 0.5;

	float height = max_height;

	float x = uv.x * freq - iTime * speed + offset;

	uv.y += max_height * (0.1 + abs(sin(iTime * 20.))) * (abs((mod((uv.x - offset * 2. ) / 0.25, 1.) - 0.5)) - 0.25);

	float f = thickness / abs(uv.y);
	f = pow(f, BLOOM);

	return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float f = beam(uv, MAX_HEIGHT, iTime, SPEED, FREQ, THICKNESS) +
			  beam(uv, MAX_HEIGHT, iTime * 1.2, SPEED, FREQ * 17.0, THICKNESS * 0.5) + 
			  beam(uv, MAX_HEIGHT, iTime * 1.7, SPEED, FREQ * 13.0, THICKNESS * 0.8);

    fragColor = vec4(f * vec3(0.1, 0.05, 0.45), 1.0);
}
