#define PI radians(180.0)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float m = sin(iTime) + 1.5;
    float n = 3.5;

    vec2 squreUV = (fragCoord - iResolution.xy * vec2(0.5)) / vec2(400.0, 400.0);
    float tmp = sin(PI * n * squreUV.x) * sin(PI * m * squreUV.y) + sin(PI * squreUV.x * m) * sin(PI * n * squreUV.y);

    tmp = 2.0 - abs(tmp);
    tmp -= 1.8;
    tmp = clamp(tmp, 0.0, 1.0);

    vec3 finalColor = mix(vec3(0.0, 0.0, 0.0), vec3(5, 2.5, 10), tmp);
    finalColor *= vec3(1.5);
    fragColor = vec4(finalColor, 1.0);
}
