void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord - (iResolution.xy / vec2(2.0));
    float jTime = mod(iTime, 261.8)+4.0;
   	coord *= pow(1.1, jTime);
    float x = float(coord.x);
    float y = float(coord.y);
    float r2 = abs((x*x+y*y)/x);
    float r3 = abs((x*x+y*y)/y);
    float r4 = abs((x*x+y*y)/(x-y))*sqrt(2.0);
    float r5 = abs((x*x+y*y)/(x+y))*sqrt(2.0);
    int a = int(floor(r2*pow(16.0, 6.0-ceil(log2(r2)/4.0))));  
    int b = int(floor(r3*pow(16.0, 6.0-ceil(log2(r3)/4.0))));
    int c = int(floor(r4*pow(16.0, 6.0-ceil(log2(r4)/4.0))));  
    int d = int(floor(r5*pow(16.0, 6.0-ceil(log2(r5)/4.0))));
    int e=(a|b)^(c|d);
    fragColor = vec4(
        float((e >> 16)& 255) / 255.0,
        float((e >> 8) & 255) / 255.0,
        float((e >> 0) & 255) / 255.0,
        1.0
    );
}
