void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);

    uv *= 10.;

    vec2 toCenter = vec2(0.)-uv;
    float p = smoothstep(0.2, 0.23 , length(toCenter));    

    //col.rgb += p;


    float s = sin(uv.x);
    float c = cos(uv.y);

    mat2 rot = mat2(c, -s, s, c);
    uv *= rot/uv.x;

    float m = smoothstep(0.2, 0.22, uv.x);
    m -= uv.x;


    float t = iTime * 0.4;
    vec3 base = sin(t*5.*vec3(.345, .456, .657))*.4 + .6;
    col = m*base;


    col += texture(iChannel0, uv*2.-iTime*.03).rgb;

    col /= 2.;

    float s2 = sin(iTime)+.3;

    col *= cos(s2)+.3;

    fragColor = vec4(col,1.0);
}
